class PlantComponentTemperatureSource(IDFBaseModel):
    """Simulates an object of pre-determined (constant or scheduled) source
    temperature The object introduces fluid into the plant loop at the specified
    temperature and at the same flow rate as the fluid enters the component
    Fluid entering the component vanishes equivalent to the relief air in an air
    system"""

    _idf_object_type: ClassVar[str] = 'PlantComponent:TemperatureSource'
    name: str = Field(..., json_schema_extra={'note': 'Component Name'})
    inlet_node: str = Field(
        ..., json_schema_extra={'note': 'Name of the source inlet node'}
    )
    outlet_node: str = Field(
        ..., json_schema_extra={'note': 'Name of the source outlet node'}
    )
    design_volume_flow_rate: float | Literal['Autosize'] = Field(
        ...,
        json_schema_extra={
            'units': 'm3/s',
            'note': 'The design volumetric flow rate for this source',
        },
    )
    temperature_specification_type: Literal['Constant', 'Scheduled'] | None = Field(
        default=None
    )
    source_temperature: float | None = Field(
        default=None,
        json_schema_extra={
            'units': 'C',
            'note': 'Used if Temperature Specification Type = Constant',
        },
    )
    source_temperature_schedule_name: ScheduleNamesRef | None = Field(
        default=None,
        json_schema_extra={
            'units': 'C',
            'object_list': ['ScheduleNames'],
            'note': 'Used if Temperature Specification Type = Scheduled',
        },
    )
